  // }

useEffect(()=>{
  fetch("https://api.weatherapi.com/v1/current.json?key=d12f8ac96da344e0a93141803210408&q=Mumbai")
  .then((res)=res.json())
  .then((data)=>{
    console.log(data);
  })
},[]);

  return (

    <div class="app">
      <header>

        <div class="col">
          <input id="input" type="text" placeholder="enter city" value={city}
            onChange={(e) => {
              setCity(e.target.value)
              console.log(city);
            }} />
          <button id="submit"
           onClick={()=>{handleSubmit(city)}}>go</button>

          <div class="row">
            <h id="temp">temp</h>
          </div>
        </div>
      </header>
      <div class="wrapper">
        <div class="cube">
          <div class="cube-row">
            <h1>description</h1>
            <ion-icon name="information-circle"></ion-icon>
          </div>
          <div class="cube-row">
            <p id="desc"></p>
          </div>
        </div>
        <div class="cube">
          <div class="cube-row">
            <h1>feels like</h1>
            <ion-icon name="thermometer"></ion-icon>
          </div>
          <div class="cube-row">
            <p id="feels-like"></p>
          </div>

        </div>
        <div class="cube">
          <div class="cube-row">
            <h1>humidity</h1>
            <ion-icon name="water"></ion-icon>
          </div>
          <div class="cube-row">
            <p id="humidity"></p>
          </div>

        </div>
        <div class="cube">
          <div class="cube-row">
            <h1>wind speed</h1>
            <ion-icon name="rainy"></ion-icon>
          </div>
          <div class="cube-row">
            <p id="wind-speed"></p>
          </div>

        </div>
      </div>
    </div>



  );
}

export default App;
